function addClassPath($path){
	if(!isset($_SESSION["phynx_addClassPaths"]))
		$_SESSION["phynx_addClassPaths"] = array();
	
	if($path[strlen($path) - 1] != "/")
		$path .= "/";
	
	if(!in_array($path, $_SESSION["phynx_addClassPaths"]))
		$_SESSION["phynx_addClassPaths"][] = $path;
}

function registerClassPath($className, $classPath){
	if(!defined("PHYNX_SESSION_DONE"))
		return;
	
	if(!isset($_SESSION["classPaths"]) OR !is_array($_SESSION["classPaths"]))
		$_SESSION["classPaths"] = array();
		
	if(!isset($_SESSION["classPaths"][$className]))
		$_SESSION["classPaths"][$className] = "";
	
	$_SESSION["classPaths"][$className] = $classPath;
}

function registerUnrealClass($className){
	if(!defined("PHYNX_SESSION_DONE"))
		return;
	
	if(!isset($_SESSION["classPathsUnreal"]) OR !is_array($_SESSION["classPathsUnreal"]))
		$_SESSION["classPathsUnreal"] = array();
		
	if(!isset($_SESSION["classPathsUnreal"][$className]))
		$_SESSION["classPathsUnreal"][$className] = "";
	
	$_SESSION["classPathsUnreal"][$className] = "unreal";
}

function phynxParseStr($query){ //fools mod_security
	$return = array();

	$ex = explode("&", $query);

	foreach($ex AS $parameter){
		$ex2 = explode("=", $parameter);
		$name = $ex2[0];
		unset($ex2[0]);

		$return[$name] = implode("=", $ex2);
	}

	return $return;
}

function findClass($class_name){
	if(strpos($class_name, 'PHPExcel') === 0)
		return false;
	
	$root = str_replace("system".DIRECTORY_SEPARATOR."basics.php", "", __FILE__);
	
	if(!defined("PHYNX_SESSION_DONE")){
		$defaults = array(
			"Session" => $root."classes/backend/Session.class.php",
			"PersistentObject" => $root."classes/backend/PersistentObject.class.php",
			"User" => $root."classes/backend/User.class.php",
			"Attributes" => $root."classes/backend/Attributes.class.php",
			"SysMessages" => $root."classes/toolbox/SysMessages.class.php",
			"Applications" => $root."classes/frontend/Applications.class.php",
			"JSLoader" => $root."classes/frontend/JSLoader.class.php",
			"AppPlugins" => $root."classes/frontend/AppPlugins.class.php",
			"BackgroundPluginState" => $root."classes/backend/BackgroundPluginState.class.php"
		);
		
		if(isset($defaults[$class_name])){
			require_once $defaults[$class_name];
			return 1;
		}
	}
	
	#echo "<br>".$class_name.":";
	/*if(false AND isset($_SESSION["classPathsUnreal"]) AND is_array($_SESSION["classPathsUnreal"]) AND isset($_SESSION["classPathsUnreal"][$class_name])) {
		if(!isset($_SESSION["classPaths"][$class_name])){
			echo "unreal!";
			throw new ClassUnrealException($class_name);
		}
	}*/
	
	
	if(isset($_SESSION["classPaths"]) AND is_array($_SESSION["classPaths"]) AND isset($_SESSION["classPaths"][$class_name])) {
		$path = $_SESSION["classPaths"][$class_name];

		if(file_exists($path))
			require_once($path);
		else
			throw new ClassNotFoundException($class_name);
		return 1;
	}
	
	$standardPaths = array();
	$standardPaths[] = $root."classes/backend/";
	$standardPaths[] = $root."classes/frontend/";
	$standardPaths[] = $root."classes/toolbox/";
	$standardPaths[] = $root."classes/interfaces/";
	$standardPaths[] = $root."libraries/";
	$standardPaths[] = $root."libraries/fpdf/";
	$standardPaths[] = $root."libraries/iban/";
	$standardPaths[] = $root."customizer/"; //first look here for customizers!
	$standardPaths[] = $root."specifics/";
	$standardPaths[] = $root."classes/exceptions/";
	$standardPaths[] = $root."libraries/geshi/";
	if(isset($_SESSION["phynx_addClassPaths"]) AND is_array($_SESSION["phynx_addClassPaths"]))
		$standardPaths = array_merge($standardPaths, $_SESSION["phynx_addClassPaths"]);
	
	foreach($standardPaths AS $v){
		#echo $class_name." 1<br>";
		$path = "$v".$class_name.'.class.php';
			
		if(is_file($path)) {
			require_once $path;
			registerClassPath($class_name, $path);
			return 1;
		}
	}
	
	if(isset($_SESSION["CurrentAppPlugins"]) AND is_object($_SESSION["CurrentAppPlugins"]) AND count($_SESSION["CurrentAppPlugins"]->getFolders()) > 0) {

		foreach($_SESSION["CurrentAppPlugins"]->getFolders() as $key => $value){
			$path = $root."plugins/$value/$class_name.class.php";

			if(is_file($path)){
				require_once $path;
				registerClassPath($class_name, $path);
				return 1;
			}
			if($_SESSION["applications"]->getActiveApplication() != "nil"){
				$path = $root."".$_SESSION["applications"]->getActiveApplication()."/$value/$class_name.class.php";

				if(is_file($path)){
					require_once $path;
					registerClassPath($class_name, $path);
					return 1;
				}
			}
			
			$path = $root."customer/$value/$class_name.class.php";

			if(is_file($path)){
				require_once $path;
				registerClassPath($class_name, $path);
				return 1;
			}

		}
	} else {
		$fp = opendir($root."plugins/");
		while(($file = readdir($fp)) !== false) {
			if($file == "." OR $file == "..")
				continue;
			
			if(is_dir($root."plugins/$file")) {

				$fp2 = opendir($root."plugins/$file/");
				while(($file2 = readdir($fp2)) !== false) {
					if(!is_dir($root."plugins/$file/$file2"))
						continue;
					
					$path = $root."plugins/$file/$file2/$class_name.class.php";
					if(is_file($path)){
						require_once $path;
						registerClassPath($class_name, $path);
						return 1;
					}
				}

			}
		}
	}

	if($class_name == "FPDF"){
		require_once $root."libraries/fpdf/fpdf.php";
		return 1;
	}

	if($class_name == "htmlMimeMail5"){
		require_once $root."libraries/mailer/htmlMimeMail5.php";
		return 1;
	}

	if($class_name == "PHPMailer"){
		require_once $root."libraries/mailer/PHPMailer.class.php";
		return 1;
	}

	if(preg_match("/^i[A-Z].*/", $class_name)) {
		$_SESSION["messages"]->addMessage("Warning: Creating interface $class_name");
		eval('interface '.$class_name.' { } ');
	} else {
		registerUnrealClass($class_name);
		
		eval('class '.str_replace("\\", "_", $class_name).' { '.
		'    public function __construct() { '.
		'        throw new ClassNotFoundException("'.$class_name.'"); '.
		'    } '.
		'} ');
	}
}

function phynx_mb_str_pad($input, $pad_length, $pad_string = " ", $pad_style = STR_PAD_RIGHT, $encoding="UTF-8") {
   return str_pad($input, strlen($input)-mb_strlen($input, $encoding)+$pad_length, $pad_string, $pad_style);
} 

function emoFatalError($excuse, $message, $title, $showErrors = true, $mode = "warning") {
	$errors = "";

	if(isset($_SESSION["phynx_errors"]) AND count($_SESSION["phynx_errors"]) > 0){
		$errors .= "<h2>Es sind PHP-Fehler aufgetreten:</h2><ol class=\"errors\">";
		foreach($_SESSION["phynx_errors"] AS $error)
			$errors .= "<li>".$error[0].": ".$error[1]."</li>";
		
		$errors .= "</ol>";
	}
	
	$image = "";
	if($mode == "warning")
		$image = '<img src="data:image/png;base64," style="float:left;margin-right:15px;" />';
	
	if($mode == "ok")
		$image = '<img src="data:image/png;base64," style="float:left;margin-right:15px;" />';
	
	
	
	if(!$showErrors)
		$errors = "";

		die('<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Strict//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-strict.dtd">
<html xmlns="http://www.w3.org/1999/xhtml">
	<head>
		<title>'.$title.'</title>
		<style type="text/css">
			* {
				padding:0px;
				margin:0px;
			}

			body {
				font-size:14px;
				font-family:sans-serif;
				background-color:#d8d8d8;
				color:black;
			}

			p {
				padding:5px;
				line-height:1.5;
			}

			div {
				padding:10px;
			}

			li {
				margin-left:20px;
				margin-top:10px;
				line-height:1.5;
			}

			.errors li {
				font-size:0.8em;
			}

			ol {
				margin-left:10px;
			}

			h1 {
				margin-bottom:5px;
			}

			h2 {
				margin-bottom:5px;
				margin-top:20px;
				clear:both;
			}

			pre {
				font-size:10px;
			}

			.backgroundColor0 {
				background-color:white;
			}
		</style>

	</head>
	<body>
		<div class="backgroundColor0">
			
			'.$image.'

			<h1>'.$excuse.'</h1>
			<p style="margin-left:80px;">'.$message.'</p>
			'.$errors.'
		</div>
	</body>
</html>');
}
